// 此文件为特殊String类型声明文件, 里面已经存在的类型不能更改, 因为要兼容老用户
// 可以在此基础上添加新的特殊String类型
declare namespace HBuilderX {
    // script使用的特殊String
    // html相关
    export  interface  AttrString              extends  String  {}
    export  interface  AttrValueString         extends  String  {}
    export  interface  ClassString             extends  String  {}
    export  interface  IDString                extends  String  {}
    export  interface  HTMLEventString         extends  String  {}
    export  interface  HTMLElementProperties   extends  String  {}
    export  interface  HTMLStyleObjectString   extends  String  {}
    // css相关
    export  interface  cssPropertyString       extends  String  {}
    export  interface  cssPropertyValueString  extends  String  {}
    export  interface  cssSelectorString       extends  String  {}
    export  interface  ColorString             extends  String  {}

    // js相关
    export  interface  ParentField             extends  String  {}
    export  interface  RequireCommonString     extends  String  {}  
    export  interface  ValidateFunctionString  extends  String  {}
    export  interface  VueI18NKeyString        extends  String  {}

    // json相关
    export  interface  SchemaField             extends  String  {}


    // 路径提示, 特殊string    
    // 历史已经存在的路径类型, 部分新特殊String实现内容可能和老的相同
    // 比如ImageURIString和TagImageURIString, TagImageURIString更精准, ImageURIString覆盖范围更广泛
    // 用哪个都行, 这两个目前实现方式是一样的, 后续可能在能够提示的文件类型上有细微的差别
    export  interface  URIString               extends  String  {}      //全路径
	export  interface  CSSURIString            extends  String  {}      //css文件路径
	export  interface  JSURIString             extends  String  {}      //js文件路径
	export  interface  HTMLURIString           extends  String  {}      //html文件路径
	export  interface  ImageURIString          extends  String  {}      //web支持的图片文件路径
    export  interface  PageURIString           extends  String  {}      //uni-app中页面路径
    export  interface  NPageURIString          extends  String  {}      //uni-app中页面路径

    // 新增的路径类型
    export  interface  CssImportURIString      extends  String  {}      //css可以导入的文件路径
    export  interface  ScssImportURIString     extends  String  {}      //scss可以导入的文件路径
    export  interface  LessImportURIString     extends  String  {}      //less可以导入的文件路径
    
    export  interface  ImportURIString         extends  String  {}      

    export  interface  FontURIString           extends String   {}      //提示字体的路径
    export  interface  AudioURIString          extends String   {}      //支持的音频文件路径
    export  interface  VideoURIString          extends String   {}      //支持的视频文件路径
    
    export  interface  VueURIString            extends String   {}      //vue文件路径

    // unicloud相关
    export  interface  ClientDBActionString    extends  String  {}
    export  interface  CloudFunctions          extends  String  {}
    export  interface  CloudObjects            extends  String  {}
    export  interface  DBCollectionString      extends  String  {}
    export  interface  DBFieldOrderByString    extends  String  {}
    export  interface  DBFieldString           extends  String  {}
    export  interface  JQLString               extends  String  {}


    // 暂时未找到使用位置, 或实现不规范的特殊String
    export  interface  ModuleURIString         extends  String  {}
    export  interface  CharsetString           extends  String  {}
    export  interface  CSSString               extends  String  {}
    export  interface  EventString             extends  String  {}
    export  interface  FontString              extends  String  {}
    export  interface  HTMLString              extends  String  {}
    export  interface  LengthString            extends  String  {}
    export  interface  ModuleString            extends  String  {}
    export  interface  MultimediaString        extends  String  {}
    export  interface  SqlString               extends  String  {}
    export  interface  TagString               extends  String  {}
    export  interface  TimeString              extends  String  {}
    export  interface  TypeString              extends  String  {}
    export  interface  ValueString             extends  String  {}
    export  interface  VuexMutationMethods     extends  String  {}
    export  interface  UniLivePlayerURIString  extends  String  {}
    
	// vue相关
	export  interface  VueDataString           extends  String  {}
	export  interface  VueEventString          extends  String  {}
	// vuex相关
	export  interface  VuexDispatchString      extends String {}
	export  interface  VuexCommitString        extends String {}
}
